mapper.select:
select questionNum, questionType, wname, title, content, phone, wdate
from question
where questionNum = #{questionNum}

mapper.update:
update question
set title = #{title},
    content = #{content}, 
    phone = #{phone, jdbcType=VARCHAR}
where questionNum = #{questionNum}

mapper.select:
select count(*) from question
where questionNum = #{questionNum} 
and passwd = #{passwd}
