mapper.update:
set revtitle = #{revtitle},
    revcontent = #{revcontent},
    revstar = #{revstar}
where hnum = #{hnum} and revnum = #{revnum}

mapper.select:
	select revtitle, revcontent, revstar, revdate, r
	from(
	    select revtitle, revcontent, revstar, revdate, rownum as r
	    from(
	        select revtitle, revcontent, revstar, revdate
	        from review
	    where hnum = #{hnum}
	    order by revnum desc
	    )
	)
	                                                                        
   where r>=#{sno} and r<=#{eno}
