mapper.select:
		select hnum, hname, hfname, r
		from(
			select hnum, hname, hfname, rownum as r
			from(
				select hnum, hname, hfname
				from hotel
				
					
						
							name like '%'||#{word}||'%'
						
					
				
		ORDER BY hnum DESC
				)	
			)                                                                            
                                                                           
   where r>=#{sno} and r<=#{eno}
